// Navbar.js – ניווט עם לוג בצבע כהה
import React from 'react';

export default function Navbar({ landingCount, alienCount, onStart, onDeleteLocal, onDeleteRemote, onSearch, log, showLog, toggleLog }) {
  return (
    <div style={{
      backgroundColor: 'black', color: 'white', padding: '10px 20px', display: 'flex', justifyContent: 'space-between', alignItems: 'center'
    }}>
      <div>
        🛸 <b>{landingCount}</b> נחיתות | 👾 <b>{alienCount}</b> חייזרים
      </div>
      <div>
        <button onClick={onStart}>צור נחיתה</button>
        <button onClick={onDeleteLocal}>מחק מקומית</button>
        <button onClick={onDeleteRemote}>מחק מרחוק</button>
        <input placeholder="חפש לפי מספר נחיתה" onChange={e => onSearch(e.target.value)} />
        <button onClick={toggleLog}>{showLog ? 'הסתר לוג' : 'הצג לוג'}</button>
      </div>
      {showLog && (
        <div style={{
          position: 'fixed', top: '60px', right: '10px', width: '300px', maxHeight: '60vh', overflowY: 'auto',
          backgroundColor: '#eee', border: '1px solid black', padding: '10px', zIndex: 1000, color: '#222', fontFamily: 'monospace', fontSize: '13px'
        }}>
          <b style={{ color: '#000' }}>לוג פעולות:</b>
          <ul style={{ padding: 0, margin: 0, listStyleType: 'none' }}>
            {log.map((item, i) => (
              <li key={i}>{item}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}
