import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, CircleMarker, Polyline, useMapEvents } from 'react-leaflet';
import axios from 'axios';
import polyline from 'polyline';

const center = [31.5, 34.8];
const OSRM_URL = "https://router.project-osrm.org";

const ClickHandler = ({ setLanding }) => {
  useMapEvents({
    click(e) {
      setLanding(e.latlng);
    },
  });
  return null;
};

export default function App() {
  const [landing, setLanding] = useState(null);
  const [aliens, setAliens] = useState([]);

  // יצירת חייזרים בנקודת הנחיתה
  useEffect(() => {
    if (!landing) return;

    const createAliens = () => {
      const directions = [0, 45, 90, 135, 180, 225, 270, 315];
      const alienPromises = directions.map(async (angle) => {
        const target = [
          landing.lat + 0.05 * Math.cos(angle),
          landing.lng + 0.05 * Math.sin(angle),
        ];
        const route = await getRoute([landing.lat, landing.lng], target);
        return {
          route,
          positionIdx: 0,
        };
      });

      Promise.all(alienPromises).then(setAliens);
    };

    createAliens();
  }, [landing]);

  // קבלת מסלול מ-OSRM
  const getRoute = async (from, to) => {
    const res = await axios.get(
      `${OSRM_URL}/route/v1/driving/${from[1]},${from[0]};${to[1]},${to[0]}?overview=full&geometries=polyline`
    );
    return polyline.decode(res.data.routes[0].geometry).map(coord => [coord[0], coord[1]]);
  };

  // תנועת החייזרים באופן רציף
  useEffect(() => {
    const interval = setInterval(() => {
      setAliens(prevAliens =>
        prevAliens.map(async alien => {
          let newIdx = alien.positionIdx + 1;

          if (newIdx >= alien.route.length) {
            // נקודת הסיום של המסלול
            const currentPos = alien.route[alien.route.length - 1];

            // בקשת מסלול המשך מנקודת הסיום
            let newRoute = await getRoute(currentPos, [
              currentPos[0] + (Math.random() - 0.5) / 10,
              currentPos[1] + (Math.random() - 0.5) / 10,
            ]);

            if (newRoute.length < 2) {
              // אם אין המשך - סיבוב פרסה
              newRoute = alien.route.slice().reverse();
            }

            return { route: newRoute, positionIdx: 0 };
          }

          return { ...alien, positionIdx: newIdx };
        })
      );
    }, 1000);

    return () => clearInterval(interval);
  }, []);

  return (
    <MapContainer center={center} zoom={10} style={{ height: '100vh' }}>
      <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
      <ClickHandler setLanding={setLanding} />
      {landing && <Marker position={landing} />}

      {aliens.map((alien, idx) => (
        <React.Fragment key={idx}>
          <Polyline positions={alien.route} color="purple" dashArray="3" />
          <CircleMarker
            center={alien.route[alien.positionIdx]}
            radius={6}
            color="red"
          />
        </React.Fragment>
      ))}
    </MapContainer>
  );
}
