import React, { useState, useEffect } from 'react';
import axios from 'axios';

const API_BASE = "https://invasion-api.onrender.com";

export default function Navbar({ onActivateCreate, setHighlightLanding }) {
  const [landingCount, setLandingCount] = useState(0);
  const [alienCount, setAlienCount] = useState(0);
  const [search, setSearch] = useState("");
  const [searchTriggered, setSearchTriggered] = useState(false);

  useEffect(() => {
    const interval = setInterval(async () => {
      try {
        const res = await axios.get(`${API_BASE}/api/invasion`);
        const features = res.data.features;

        const landings = features.filter(f => f.properties?.type === "landing");
        const aliens = features.filter(f => f.properties?.type === "alien");

        setLandingCount(landings.length);
        setAlienCount(aliens.length);

        if (searchTriggered && search) {
          const match = landings.find(l =>
            (l.properties?.locationName || "").toLowerCase().includes(search.toLowerCase()) ||
            (l.properties?.landingCode || "").toLowerCase().includes(search.toLowerCase())
          );

          if (match) {
            setHighlightLanding({
              id: match.properties.id,
              lat: match.geometry.coordinates[1],
              lng: match.geometry.coordinates[0],
              name: match.properties.locationName,
              code: match.properties.landingCode
            });
          }
        }
      } catch (err) {
        console.error("❌ Failed to fetch invasion stats:", err.message);
      }
    }, 1000);

    return () => clearInterval(interval);
  }, [search, searchTriggered, setHighlightLanding]);

  const handleSearchSubmit = (e) => {
    e.preventDefault();
    setSearchTriggered(true);
  };

  return (
    <div style={{
      height: "50px",
      backgroundColor: "black",
      color: "white",
      display: "flex",
      alignItems: "center",
      justifyContent: "space-between",
      padding: "0 20px",
      fontFamily: "sans-serif"
    }}>
      <div style={{ fontWeight: "bold", fontSize: "18px" }}>
        Invasion Monitor
      </div>
      <form onSubmit={handleSearchSubmit} style={{ display: 'flex', alignItems: 'center' }}>
        <div>
          🛸 {landingCount} | 👽 {alienCount}
          <input
            type="text"
            placeholder="Search landings..."
            value={search}
            onChange={(e) => {
              setSearch(e.target.value);
              setSearchTriggered(false);
              setHighlightLanding(null); // Clear highlight when editing
            }}
            style={{
              marginLeft: "10px",
              padding: "4px 8px",
              borderRadius: "5px",
              border: "1px solid gray",
              fontSize: "12px"
            }}
          />
          <button type="submit" style={{
            marginLeft: "5px",
            padding: "4px 8px",
            borderRadius: "5px",
            border: "1px solid white",
            backgroundColor: "white",
            color: "black",
            fontSize: "12px",
            cursor: "pointer"
          }}>
            🔍
          </button>
        </div>
      </form>
      <button
        onClick={onActivateCreate}
        style={{
          background: "white",
          color: "black",
          border: "none",
          padding: "8px 12px",
          borderRadius: "5px",
          cursor: "pointer"
        }}
      >
        Create Landing ⚡
      </button>
    </div>
  );
}
