import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, CircleMarker, useMapEvents } from 'react-leaflet';
import * as turf from '@turf/turf';
import axios from 'axios';

const center = [31.5, 34.8]; // מרכז ישראל

const ClickHandler = ({ setLanding }) => {
  useMapEvents({
    click(e) {
      setLanding(e.latlng);
    },
  });
  return null;
};

export default function App() {
  const [landing, setLanding] = useState(null);
  const [invasionPoints, setInvasionPoints] = useState([]);

  useEffect(() => {
    if (landing) {
      // יצירת נקודות רנדומליות להתפשטות
      const points = [landing];
      for (let i = 0; i < 50; i++) {
        const angle = Math.random() * 360;
        const distance = Math.random() * 0.1; // עד כ-10 ק"מ
        const newPoint = turf.destination(
          [landing.lng, landing.lat],
          distance,
          angle,
          { units: "kilometers" }
        ).geometry.coordinates;
        points.push({ lat: newPoint[1], lng: newPoint[0] });
      }
      setInvasionPoints(points);

      // שליחת נתונים לשרת
      const geoJSON = {
        type: "FeatureCollection",
        features: points.map(p => ({
          type: "Feature",
          geometry: { type: "Point", coordinates: [p.lng, p.lat] },
          properties: {},
        })),
      };
      axios.post('http://localhost:5000/api/invasion', geoJSON);
    }
  }, [landing]);

  return (
    <MapContainer center={center} zoom={8} style={{ height: "100vh" }}>
      <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
      <ClickHandler setLanding={setLanding} />
      {landing && <Marker position={landing} />}
      {invasionPoints.map((point, idx) => (
        <CircleMarker
          key={idx}
          center={point}
          radius={5}
          color="red"
        />
      ))}
    </MapContainer>
  );
}
