// App.js – תיקון תצוגת אייקונים גם בנחיתות מרחוק, וגם של חייזרים
import React, { useEffect, useState } from 'react';
import { MapContainer, TileLayer, Marker, Polyline, useMapEvents } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import axios from 'axios';
import polyline from 'polyline';
import L from 'leaflet';
import Navbar from './Navbar';

const API = 'https://invasion-api.onrender.com';

const alienIcon = (number) => L.divIcon({
  html: `<div style="font-size:24px;">👽<span style="color:black; font-weight:bold; font-size:14px;">${number}</span></div>`,
  className: 'alien-icon',
  iconSize: [30, 30],
});

const landingIcon = L.divIcon({
  html: '<div style="font-size:28px;">🛸</div>',
  className: 'landing-icon',
  iconSize: [30, 30],
});

export default function App() {
  const [landings, setLandings] = useState([]);
  const [placingLanding, setPlacingLanding] = useState(false);
  const [localLandingId, setLocalLandingId] = useState(null);
  const [log, setLog] = useState([]);
  const [showLog, setShowLog] = useState(false);
  const [searchId, setSearchId] = useState(null);
  const [aliens, setAliens] = useState([]);

  const fetchLandings = async () => {
    const res = await axios.get(`${API}/api/landings`);
    setLandings(res.data);
    logEvent("📥 רענון נחיתות מהשרת");
  };

  const fetchAliens = async () => {
    const res = await axios.get(`${API}/api/aliens`);
    const decoded = res.data.map(a => ({
      ...a,
      decodedRoute: polyline.decode(a.route || ''),
      positionIdx: a.positionIdx || 0
    })).filter(a => a.decodedRoute.length > 0);
    setAliens(decoded);
  };

  useEffect(() => {
    fetchLandings();
    fetchAliens();
    const interval = setInterval(() => {
      setAliens(prev => prev.map(alien => ({
        ...alien,
        positionIdx: Math.min(alien.positionIdx + 1, alien.decodedRoute.length - 1)
      })));
    }, 1500);
    return () => clearInterval(interval);
  }, []);

  const logEvent = (msg) => {
    const time = new Date().toLocaleTimeString();
    setLog(prev => [`${time} – ${msg}`, ...prev]);
  };

  const handleStartPlacing = () => {
    setPlacingLanding(true);
    logEvent("🛸 הפעלת מצב יצירת נחיתה");
  };

  const handleDeleteLocal = () => {
    if (localLandingId) {
      setLandings(prev => prev.filter(l => l.id !== localLandingId));
      logEvent(`❌ נחיתה ${localLandingId} נמחקה מקומית`);
      setLocalLandingId(null);
    }
  };

  const handleDeleteRemote = async () => {
    if (localLandingId) {
      await axios.delete(`${API}/api/landing/${localLandingId}`);
      logEvent(`💥 נחיתה ${localLandingId} נמחקה מהשרת`);
      await fetchLandings();
      await fetchAliens();
      setLocalLandingId(null);
    }
  };

  const handleSearchLanding = (val) => {
    const id = parseInt(val);
    if (!isNaN(id)) setSearchId(id);
    else setSearchId(null);
  };

  const ClickHandler = () => {
    useMapEvents({
      click: async (e) => {
        if (!placingLanding) return;

        const res = await axios.post(`${API}/api/landing`, {
          lat: e.latlng.lat,
          lng: e.latlng.lng
        });

        setLocalLandingId(res.data.id);
        logEvent(`🆕 נוצרה נחיתה חדשה (${res.data.id})`);

        await axios.post(`${API}/api/aliens`, {
          landingId: res.data.id,
          lat: res.data.lat,
          lng: res.data.lng
        });
        logEvent(`👾 נוצרו 8 חייזרים לנחיתה ${res.data.id}`);

        await fetchLandings();
        await fetchAliens();
        setPlacingLanding(false);
      }
    });
    return null;
  };

  return (
    <div>
      <Navbar
        landingCount={landings.length}
        alienCount={aliens.length}
        onStart={handleStartPlacing}
        onDeleteLocal={handleDeleteLocal}
        onDeleteRemote={handleDeleteRemote}
        onSearch={handleSearchLanding}
        log={log}
        showLog={showLog}
        toggleLog={() => setShowLog(!showLog)}
      />
      <MapContainer center={[32.1, 34.8]} zoom={11} style={{ height: '100vh', width: '100%' }}>
        <ClickHandler />
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution='&copy; OpenStreetMap contributors'
        />
        {landings.map((landing) => (
          <Marker
            key={landing.id}
            position={[landing.lat, landing.lng]}
            icon={landingIcon}
            eventHandlers={{ click: () => setLocalLandingId(landing.id) }}
          />
        ))}
        {aliens.map((alien) => (
          <React.Fragment key={alien.id}>
            <Polyline positions={alien.decodedRoute} color="purple" dashArray="3" />
            <Marker
              position={alien.decodedRoute[alien.positionIdx]}
              icon={alienIcon(alien.id)}
            />
          </React.Fragment>
        ))}
      </MapContainer>
    </div>
  );
}
