import React, { useEffect, useState } from 'react';
import { MapContainer, TileLayer, Marker, Polyline, useMapEvents } from 'react-leaflet';
import axios from 'axios';
import polyline from 'polyline';
import L from 'leaflet';
import Navbar from './Navbar';
import './App.css';

const API = 'https://invasion-api.onrender.com';

const landingIcon = L.divIcon({
  className: 'custom-icon',
  html: '🛸',
  iconSize: [32, 32],
  iconAnchor: [16, 16],
});

const alienIcon = (id) => L.divIcon({
  className: 'custom-icon',
  html: `<div style="font-size:20px; color:green;">👽<div style="font-size:10px">${id}</div></div>`,
  iconSize: [32, 32],
  iconAnchor: [16, 16],
});

export default function App() {
  const [landings, setLandings] = useState([]);
  const [aliens, setAliens] = useState([]);
  const [localLandingIds, setLocalLandingIds] = useState([]);
  const [placingLanding, setPlacingLanding] = useState(false);
  const [deleteRemoteMode, setDeleteRemoteMode] = useState(false);
  const [deleteLocalMode, setDeleteLocalMode] = useState(false);
  const [log, setLog] = useState([]);
  const [showLog, setShowLog] = useState(false);

  const logEvent = (msg) => {
    const time = new Date().toLocaleTimeString();
    setLog((prev) => [`${time} – ${msg}`, ...prev]);
  };

  const fetchLandings = async () => {
    const res = await axios.get(`${API}/api/landings`);
    setLandings(res.data);
  };

  const fetchAliens = async () => {
    const res = await axios.get(`${API}/api/aliens`);
    const decoded = res.data.map((a) => ({
      ...a,
      decodedRoute: polyline.decode(a.route || ''),
    }));
    setAliens((prev) => {
      const localAliens = prev.filter((a) => a.local);
      return [...decoded, ...localAliens];
    });
  };

  useEffect(() => {
    fetchLandings();
    fetchAliens();
    const interval = setInterval(() => {
      fetchLandings();
      fetchAliens();
    }, 2000);
    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    const interval = setInterval(() => {
      setAliens((prev) =>
        prev.map((a) => {
          const next = a.positionIdx + 1;
          if (next < a.decodedRoute.length) {
            return { ...a, positionIdx: next };
          } else {
            return { ...a, positionIdx: a.decodedRoute.length - 1 };
          }
        })
      );
    }, 1500);
    return () => clearInterval(interval);
  }, []);

  const handleStartPlacing = () => {
    setPlacingLanding(true);
    setDeleteRemoteMode(false);
    setDeleteLocalMode(false);
    document.body.className = 'create-mode';
    logEvent("🛸 מצב יצירת נחיתה הופעל");
  };

  const handleDeleteLocal = () => {
    setDeleteLocalMode(true);
    setPlacingLanding(false);
    setDeleteRemoteMode(false);
    document.body.className = 'delete-local-mode';
    logEvent("🧹 מצב מחיקה מקומית הופעל");
  };

  const handleDeleteRemote = () => {
    setDeleteRemoteMode(true);
    setPlacingLanding(false);
    setDeleteLocalMode(false);
    document.body.className = 'delete-remote-mode';
    logEvent("⚡ מצב מחיקה מרחוק הופעל");
  };

  const ClickHandler = () => {
    useMapEvents({
      click: async (e) => {
        if (placingLanding) {
          const res = await axios.post(`${API}/api/landing`, {
            lat: e.latlng.lat,
            lng: e.latlng.lng,
          });
          const newLanding = {
            id: res.data.id,
            lat: res.data.lat,
            lng: res.data.lng,
          };
          setLocalLandingIds((prev) => [...prev, newLanding.id]);
          setLandings((prev) => [...prev, newLanding]);
          logEvent(`🛸 נחיתה ${newLanding.id} נוצרה`);

          await axios.post(`${API}/api/aliens`, {
            landingId: newLanding.id,
            lat: newLanding.lat,
            lng: newLanding.lng
          });
          fetchAliens();
          setPlacingLanding(false);
          document.body.className = '';
          logEvent("👽 חייזרים שוגרו");
        } else if (deleteLocalMode || deleteRemoteMode) {
          const clicked = landings.find(
            (l) =>
              Math.abs(l.lat - e.latlng.lat) < 0.0005 &&
              Math.abs(l.lng - e.latlng.lng) < 0.0005
          );
          if (!clicked) return;

          if (deleteLocalMode && localLandingIds.includes(clicked.id)) {
            setLandings((prev) => prev.filter((l) => l.id !== clicked.id));
            setAliens((prev) => prev.filter((a) => a.landingId !== clicked.id));
            setLocalLandingIds((prev) => prev.filter((id) => id !== clicked.id));
            logEvent(`🧹 נמחקה נחיתה ${clicked.id} מקומית`);
          }

          if (deleteRemoteMode) {
            await axios.delete(`${API}/api/landing/${clicked.id}`);
            logEvent(`⚡ נמחקה נחיתה ${clicked.id} מהשרת`);
          }

          setDeleteLocalMode(false);
          setDeleteRemoteMode(false);
          document.body.className = '';
        }
      },
    });
    return null;
  };

  return (
    <div>
      <Navbar
        landingCount={landings.length}
        alienCount={aliens.length}
        onStart={handleStartPlacing}
        onDeleteLocal={handleDeleteLocal}
        onDeleteRemote={handleDeleteRemote}
        log={log}
        showLog={showLog}
        toggleLog={() => setShowLog(!showLog)}
      />
      <MapContainer center={[32.1, 34.8]} zoom={12} style={{ height: '100vh', width: '100%' }}>
        <ClickHandler />
        <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
        {landings.map((l) => (
          <Marker key={l.id} position={[l.lat, l.lng]} icon={landingIcon} />
        ))}
        {aliens.map((a) => (
          <React.Fragment key={a.id}>
            <Polyline positions={a.decodedRoute} color="purple" dashArray="3" />
            <Marker position={a.decodedRoute[a.positionIdx]} icon={alienIcon(a.id)} />
          </React.Fragment>
        ))}
      </MapContainer>
    </div>
  );
}
