import { useEffect } from 'react';

export default function AlienManager({ aliens, setAliens }) {
  useEffect(() => {
    const interval = setInterval(() => {
      setAliens(prevAliens => {
        return prevAliens.map(alien => {
          const route = alien.route;
          if (!route || route.length < 2) {
            // אם אין route – לא מזיז, מחכה לעדכון
            return alien.position
              ? { ...alien, positionIdx: 0 }
              : alien;
          }

          const nextIdx = alien.positionIdx + 1;
          if (nextIdx < route.length) {
            return { ...alien, positionIdx: nextIdx };
          } else {
            return alien; // יתחלף ע"י getRoute חדש
          }
        });
      });
    }, 1000);

    return () => clearInterval(interval);
  }, [setAliens]);

  return null;
}
