import React, { useEffect, useState } from 'react';
import { MapContainer, TileLayer, Marker, Polyline, useMapEvents } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import axios from 'axios';
import polyline from 'polyline';
import L from 'leaflet';
import Navbar from './Navbar';

const API = 'https://invasion-api.onrender.com';

const landingIcon = L.divIcon({
  className: 'custom-icon',
  html: 'ðŸ›¸',
  iconSize: [32, 32],
  iconAnchor: [16, 16],
});

const alienIcon = (id) =>
  L.divIcon({
    className: 'custom-icon',
    html: `<div style="font-size:20px; color:green;">ðŸ‘½<div style="font-size:10px">${id}</div></div>`,
    iconSize: [32, 32],
    iconAnchor: [16, 16],
  });

export default function App() {
  const [landings, setLandings] = useState([]);
  const [aliens, setAliens] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const l = await axios.get(`${API}/api/landings`);
      setLandings(l.data);
      const a = await axios.get(`${API}/api/aliens`);
      const decoded = a.data.map((alien) => ({
        ...alien,
        decodedRoute: polyline.decode(alien.route || ''),
      }));
      setAliens(decoded);
    };
    fetchData();
    const interval = setInterval(fetchData, 2000);
    return () => clearInterval(interval);
  }, []);

  return (
    <MapContainer center={[32.1, 34.8]} zoom={12} style={{ height: '100vh', width: '100%' }}>
      <TileLayer
        attribution="&copy; OpenStreetMap"
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
      />
      {landings.map((landing) => (
        <Marker key={landing.id} position={[landing.lat, landing.lng]} icon={landingIcon} />
      ))}
      {aliens.map((alien) => (
        alien.decodedRoute && alien.decodedRoute.length > 0 && (
          <React.Fragment key={alien.id}>
            <Polyline positions={alien.decodedRoute} color="purple" dashArray="3" />
            <Marker
              position={alien.decodedRoute[alien.positionIdx || 0]}
              icon={alienIcon(alien.id)}
            />
          </React.Fragment>
        )
      ))}
    </MapContainer>
  );
}
