import React from 'react';
import { MapContainer, TileLayer, Marker, Polyline, useMapEvents } from 'react-leaflet';
import L from 'leaflet';

const ClickHandler = ({ onClick }) => {
  useMapEvents({
    click(e) {
      onClick(e.latlng);
    },
  });
  return null;
};

const alienIcon = (code) => L.divIcon({
  html: `<div style="font-size:20px; font-weight:bold;">👽 <span style="font-size:14px; color:black;">${code}</span></div>`,
  className: 'alien-icon',
  iconSize: [30, 30],
});

const landingIcon = (locationName) => L.divIcon({
  html: `<div style="display:flex; flex-direction:column; align-items:center;">
    <div style="font-size:24px;">🛸</div>
    <div style="background-color:black; color:white; padding:2px 6px; border-radius:4px; font-size:12px; margin-top:2px;">${locationName}</div>
  </div>`,
  className: 'landing-icon',
  iconSize: [100, 40],
});

const flagIcon = () => L.divIcon({
  html: '🚩',
  className: 'highlight-icon',
  iconSize: [24, 24],
  iconAnchor: [12, 24]
});

export default function MapView({ center, landings, aliens, onMapClick, highlightLanding }) {
  const alienByLanding = (landingId) =>
    aliens.filter(a => a.landingId === landingId);

  return (
    <>
      <MapContainer center={center} zoom={10} style={{ height: 'calc(100vh - 50px)' }}>
        <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
        <ClickHandler onClick={onMapClick} />

        {landings.map((l) => (
          <Marker key={l.id} position={[l.lat, l.lng]} icon={landingIcon(l.name)} />
        ))}

        {aliens.map((a) => (
          <React.Fragment key={a.id}>
            <Polyline positions={a.route} color="purple" dashArray="3" />
            <Marker
              position={a.route[a.positionIdx]}
              icon={alienIcon(a.alienCode || a.id)}
            />
          </React.Fragment>
        ))}

        {highlightLanding && (
          <Marker
            key={"highlight"}
            position={[highlightLanding.lat, highlightLanding.lng]}
            icon={flagIcon()}
          />
        )}
      </MapContainer>

      {highlightLanding && (
        <div style={{
          position: "absolute",
          top: "60px",
          right: "20px",
          background: "white",
          padding: "10px",
          borderRadius: "8px",
          boxShadow: "0 2px 6px rgba(0,0,0,0.3)",
          zIndex: 999
        }}>
          <div><b>🔍 נחיתה שנמצאה:</b></div>
          <div>📍 שם: {highlightLanding.name}</div>
          <div>🔠 אות: {highlightLanding.code || "?"}</div>
          <div>👽 חייזרים: {alienByLanding(highlightLanding.id).length}</div>
        </div>
      )}
    </>
  );
}
