// App.js
import React, { useEffect, useState } from 'react';
import { MapContainer, TileLayer, Marker, Polyline, useMapEvents } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import axios from 'axios';
import polyline from 'polyline';
import L from 'leaflet';
import Navbar from './Navbar';

const API = 'https://invasion-api.onrender.com';

const landingIcon = L.divIcon({
  className: 'custom-icon',
  html: '🛸',
  iconSize: [32, 32],
  iconAnchor: [16, 16],
});

const alienIcon = (id) =>
  L.divIcon({
    className: 'custom-icon',
    html: `<div style="font-size:20px; color:green;">👽<div style="font-size:10px">${id}</div></div>`,
    iconSize: [32, 32],
    iconAnchor: [16, 16],
  });

export default function App() {
  const [landings, setLandings] = useState([]);
  const [aliens, setAliens] = useState([]);
  const [localLandingIds, setLocalLandingIds] = useState([]);
  const [placingLanding, setPlacingLanding] = useState(false);
  const [deleteRemoteMode, setDeleteRemoteMode] = useState(false);
  const [deleteLocalMode, setDeleteLocalMode] = useState(false);
  const [log, setLog] = useState([]);
  const [showLog, setShowLog] = useState(false);

  const logEvent = (msg) => {
    const time = new Date().toLocaleTimeString();
    setLog((prev) => [`${time} – ${msg}`, ...prev]);
  };

  const fetchLandings = async () => {
    const res = await axios.get(`${API}/api/landings`);
    setLandings(res.data);
  };

  const fetchAliens = async () => {
    const res = await axios.get(`${API}/api/aliens`);
    const decoded = res.data.map((a) => ({
      ...a,
      decodedRoute: polyline.decode(a.route || ''),
    }));
    setAliens((prev) => {
      const localAliens = prev.filter((a) => a.local);
      return [...decoded, ...localAliens];
    });
  };

  useEffect(() => {
    fetchLandings();
    fetchAliens();
    const interval = setInterval(() => {
      fetchLandings();
      fetchAliens();
    }, 2000);
    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    const interval = setInterval(() => {
      setAliens((prev) =>
        prev.map((a) => {
          const next = a.positionIdx + 1;
          if (next < a.decodedRoute.length) {
            return { ...a, positionIdx: next };
          } else if (a.local) {
            generateNewRouteForAlien(a);
            return { ...a, positionIdx: 0 };
          } else {
            return { ...a, positionIdx: a.decodedRoute.length - 1 };
          }
        })
      );
    }, 1500);
    return () => clearInterval(interval);
  }, []);

  const generateNewRouteForAlien = async (alien) => {
    const lastPos = alien.decodedRoute[alien.decodedRoute.length - 1];
    const angle = Math.random() * 360;
    const rad = angle * (Math.PI / 180);
    const to = [lastPos[0] + 0.05 * Math.cos(rad), lastPos[1] + 0.05 * Math.sin(rad)];
    try {
      const routeRes = await axios.get(`${API}/api/route`, {
        params: {
          fromLat: lastPos[0],
          fromLng: lastPos[1],
          toLat: to[0],
          toLng: to[1],
        },
      });
      const decoded = polyline.decode(routeRes.data.routes[0].geometry);
      alien.decodedRoute = decoded;
      alien.route = polyline.encode(decoded);
    } catch (e) {
      console.error("OSRM route error:", e.message);
    }
  };

  const ClickHandler = () => {
    useMapEvents({
      click: async (e) => {
        if (placingLanding) {
          const res = await axios.post(`${API}/api/landing`, {
            lat: e.latlng.lat,
            lng: e.latlng.lng,
          });
          const newLanding = {
            id: res.data.id,
            lat: res.data.lat,
            lng: res.data.lng,
          };
          setLocalLandingIds((prev) => [...prev, newLanding.id]);
          setLandings((prev) => [...prev, newLanding]);
          logEvent(`🛸 נחיתה ${newLanding.id} נוצרה`);

          const directions = [0, 45, 90, 135, 180, 225, 270, 315];
          const nextAlienId = aliens.length ? Math.max(...aliens.map((a) => a.id)) + 1 : 1;

          const routes = await Promise.all(
            directions.map(async (angle) => {
              const rad = angle * (Math.PI / 180);
              const to = [
                newLanding.lat + 0.05 * Math.cos(rad),
                newLanding.lng + 0.05 * Math.sin(rad),
              ];
              const routeRes = await axios.get(`${API}/api/route`, {
                params: {
                  fromLat: newLanding.lat,
                  fromLng: newLanding.lng,
                  toLat: to[0],
                  toLng: to[1],
                },
              });
              return polyline.decode(routeRes.data.routes[0].geometry);
            })
          );

          const localAliens = routes.map((route, i) => ({
            id: nextAlienId + i,
            landingId: newLanding.id,
            decodedRoute: route,
            route: polyline.encode(route),
            positionIdx: 0,
            local: true,
          }));

          setAliens((prev) => [...prev, ...localAliens]);
          setPlacingLanding(false);
          logEvent(`👽 ${localAliens.length} חייזרים שוגרו`);
          document.body.style.cursor = 'default';
        } else if (deleteLocalMode || deleteRemoteMode) {
          const clicked = landings.find(
            (l) =>
              Math.abs(l.lat - e.latlng.lat) < 0.0005 &&
              Math.abs(l.lng - e.latlng.lng) < 0.0005
          );
          if (!clicked) return;

          if (deleteLocalMode && localLandingIds.includes(clicked.id)) {
            setLandings((prev) => prev.filter((l) => l.id !== clicked.id));
            setAliens((prev) => prev.filter((a) => a.landingId !== clicked.id));
            setLocalLandingIds((prev) => prev.filter((id) => id !== clicked.id));
            logEvent(`🧹 נמחקה נחיתה ${clicked.id} מקומית`);
          }

          if (deleteRemoteMode) {
            await axios.delete(`${API}/api/landing/${clicked.id}`);
            logEvent(`⚡ נמחקה נחיתה ${clicked.id} מהשרת`);
          }

          setDeleteLocalMode(false);
          setDeleteRemoteMode(false);
          document.body.style.cursor = 'default';
        }
      },
    });
    return null;
  };

  const handleStartPlacing = () => {
    setPlacingLanding(true);
    setDeleteRemoteMode(false);
    setDeleteLocalMode(false);
    document.body.style.cursor = 'copy';
    logEvent("🛸 מצב יצירת נחיתה הופעל");
  };

  const handleDeleteLocal = () => {
    setDeleteLocalMode(true);
    setPlacingLanding(false);
    setDeleteRemoteMode(false);
    document.body.style.cursor = 'not-allowed';
    logEvent("🧹 מצב מחיקה מקומית הופעל");
  };

  const handleDeleteRemote = () => {
    setDeleteRemoteMode(true);
    setPlacingLanding(false);
    setDeleteLocalMode(false);
    document.body.style.cursor = 'crosshair';
    logEvent("⚡ מצב מחיקה מרחוק הופעל");
  };

  return (
    <div>
      <Navbar
        landingCount={landings.length}
        alienCount={aliens.length}
        onStart={handleStartPlacing}
        onDeleteLocal={handleDeleteLocal}
        onDeleteRemote={handleDeleteRemote}
        log={log}
        showLog={showLog}
        toggleLog={() => setShowLog(!showLog)}
      />
      <MapContainer center={[32.1, 34.8]} zoom={12} style={{ height: '100vh', width: '100%' }}>
        <ClickHandler />
        <TileLayer
          attribution="&copy; OpenStreetMap"
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />
        {landings.map((landing) => (
          <Marker
            key={`${landing.id}`}
            position={[landing.lat, landing.lng]}
            icon={landingIcon}
          />
        ))}
        {aliens.map((alien) => (
          <React.Fragment key={alien.id}>
            <Polyline positions={alien.decodedRoute} color="purple" dashArray="3" />
            <Marker
              position={alien.decodedRoute[alien.positionIdx]}
              icon={alienIcon(alien.id)}
            />
          </React.Fragment>
        ))}
      </MapContainer>
    </div>
  );
}
