import React from 'react';
import { MapContainer, TileLayer, Marker, Polyline, useMapEvents } from 'react-leaflet';
import L from 'leaflet';

const ClickHandler = ({ onClick }) => {
  useMapEvents({
    click(e) {
      onClick(e.latlng);
    },
  });
  return null;
};

const createEmojiIcon = (emoji, label, isLanding = false) =>
  L.divIcon({
    html: `
      <div style="display: flex; flex-direction: column; align-items: center;">
        ${
          isLanding
            ? `<div style="
                background-color: black;
                color: white;
                padding: 1px 4px;
                border-radius: 4px;
                font-size: 10px;
                margin-bottom: 2px;
                white-space: nowrap;
              ">${label}</div>`
            : ''
        }
        <div style="font-size: 24px;">${emoji}</div>
        ${
          !isLanding
            ? `<div style="
                background-color: transparent;
                color: black;
                font-size: 14px;
                margin-top: -4px;
                font-weight: bold;
                white-space: nowrap;
              ">${label}</div>`
            : ''
        }
      </div>
    `,
    className: 'emoji-icon',
    iconSize: [30, 42],
    iconAnchor: [15, 21],
    popupAnchor: [0, -30],
  });


export default function MapView({ center, landings, aliens, onMapClick }) {
  return (
    <MapContainer center={center} zoom={10} style={{ height: 'calc(100vh - 50px)' }}>
      <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
      <ClickHandler onClick={onMapClick} />

      {landings.map((l) => (
        <Marker
          key={l.id}
          position={[l.lat, l.lng]}
          icon={createEmojiIcon('ðŸ›¸', l.name, true)}
        />
      ))}

      {aliens.map((a) => (
        <React.Fragment key={a.id}>
          <Polyline positions={a.route} color="purple" dashArray="3" />
          <Marker
  position={a.route[a.positionIdx]}
  icon={createEmojiIcon('ðŸ‘½', a.alienCode || a.id, false)}
/>
        </React.Fragment>
      ))}
    </MapContainer>
  );
}
