// App.js – מחיקה מקומית ומרחוק עם שינוי סמן עכבר לאייקון ברק
import React, { useEffect, useState } from 'react';
import { MapContainer, TileLayer, Marker, Polyline, useMapEvents } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import axios from 'axios';
import polyline from 'polyline';
import L from 'leaflet';
import Navbar from './Navbar';

const API = 'https://invasion-api.onrender.com';

const alienIcon = (number) => L.divIcon({
  html: `<div style="font-size:24px;">👽<span style="color:black; font-weight:bold; font-size:14px;">${number}</span></div>`,
  className: 'alien-icon',
  iconSize: [30, 30],
});

const landingIcon = L.divIcon({
  html: '<div style="font-size:26px; transform: rotate(-20deg);">🛸</div>',
  className: 'landing-icon',
  iconSize: [30, 30],
});

function generateDestinations(lat, lng) {
  const directions = [0, 45, 90, 135, 180, 225, 270, 315];
  const step = 0.01;
  return directions.map((angle) => {
    const dx = Math.cos((angle * Math.PI) / 180) * step;
    const dy = Math.sin((angle * Math.PI) / 180) * step;
    return [lat + dy, lng + dx];
  });
}

async function getRoute(from, to) {
  try {
    const res = await axios.get(`${API}/api/route?fromLat=${from[0]}&fromLng=${from[1]}&toLat=${to[0]}&toLng=${to[1]}`);
    return polyline.decode(res.data.routes[0].geometry);
  } catch (err) {
    console.error('שגיאה בשליפת מסלול:', err.message);
    return [from, to];
  }
}

export default function App() {
  const [landings, setLandings] = useState([]);
  const [placingLanding, setPlacingLanding] = useState(false);
  const [localLandingIds, setLocalLandingIds] = useState([]);
  const [log, setLog] = useState([]);
  const [showLog, setShowLog] = useState(false);
  const [searchId, setSearchId] = useState(null);
  const [aliens, setAliens] = useState([]);
  const [selectedLandingId, setSelectedLandingId] = useState(null);
  const [deleteMode, setDeleteMode] = useState(null); // "remote" | "local" | null

  const fetchLandings = async () => {
    try {
      const res = await axios.get(`${API}/api/landings`);
      setLandings(res.data);
    } catch (err) {
      console.error('שגיאה בשליפת נחיתות:', err.message);
    }
  };

  const fetchAliens = async () => {
    try {
      const res = await axios.get(`${API}/api/aliens`);
      const serverAliens = res.data.map(a => ({
        ...a,
        decodedRoute: polyline.decode(a.route || ''),
        positionIdx: a.positionIdx || 0
      })).filter(a => a.decodedRoute.length > 0);

      setAliens(prev => {
        const localAliens = prev.filter(a => a.local);
        return [...serverAliens, ...localAliens];
      });

    } catch (err) {
      console.error('שגיאה בשליפת חייזרים:', err.message);
    }
  };

  useEffect(() => {
    fetchLandings();
    fetchAliens();

    const moveAliens = setInterval(() => {
      setAliens(prevAliens => {
        const moveAndRegenerate = async () => {
          const updated = await Promise.all(prevAliens.map(async (alien) => {
            if (alien.positionIdx < alien.decodedRoute.length - 1) {
              return { ...alien, positionIdx: alien.positionIdx + 1 };
            } else if (alien.local) {
              const start = alien.decodedRoute[alien.decodedRoute.length - 1];
              const directions = generateDestinations(start[0], start[1]);
              const destination = directions[Math.floor(Math.random() * directions.length)];
              const newRoute = await getRoute(start, destination);
              return {
                ...alien,
                decodedRoute: newRoute,
                route: polyline.encode(newRoute),
                positionIdx: 0
              };
            } else {
              return alien;
            }
          }));
          setAliens(updated);
        };
        moveAndRegenerate();
        return prevAliens;
      });
    }, 1500);

    const polling = setInterval(() => {
      fetchLandings();
      fetchAliens();
    }, 1500);

    return () => {
      clearInterval(moveAliens);
      clearInterval(polling);
    };
  }, []);

  useEffect(() => {
    if (deleteMode) {
      document.body.style.cursor = 'crosshair';
    } else {
      document.body.style.cursor = 'default';
    }
  }, [deleteMode]);

  const logEvent = (msg) => {
    const time = new Date().toLocaleTimeString();
    setLog(prev => [`${time} – ${msg}`, ...prev]);
  };

  const handleDeleteClick = (landing) => {
    if (deleteMode === 'remote') {
      axios.delete(`${API}/api/landing/${landing.id}`);
      setLandings(prev => prev.filter(l => l.id !== landing.id));
      setAliens(prev => prev.filter(a => !a.route.startsWith(`${landing.lat},${landing.lng}`)));
      logEvent(`⚡️ נחיתה ${landing.id} נמחקה מהשרת`);
    } else if (deleteMode === 'local' && localLandingIds.includes(landing.id)) {
      setLandings(prev => prev.filter(l => l.id !== landing.id));
      setLocalLandingIds(ids => ids.filter(id => id !== landing.id));
      setAliens(prev => prev.filter(a => !a.local));
      logEvent(`🧹 נחיתה ${landing.id} נמחקה מקומית`);
    }
    setDeleteMode(null);
  };

  return (
    <div>
      <Navbar
        landingCount={landings.length}
        alienCount={aliens.length}
        onStart={handleStartPlacing}
        onDeleteLocal={() => setDeleteMode('local')}
        onDeleteRemote={() => setDeleteMode('remote')}
        onSearch={handleSearchLanding}
        log={log}
        showLog={showLog}
        toggleLog={() => setShowLog(!showLog)}
      />
      <MapContainer center={[32.1, 34.8]} zoom={11} style={{ height: '100vh', width: '100%' }}>
        <ClickHandler />
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution='&copy; OpenStreetMap contributors'
        />
        {landings.map((landing) => (
          <Marker
            key={`landing-${landing.id}`}
            position={[landing.lat, landing.lng]}
            icon={landingIcon}
            eventHandlers={{
              click: () => deleteMode ? handleDeleteClick(landing) : setSelectedLandingId(landing.id)
            }}
          />
        ))}
        {aliens.map((alien) => (
          <React.Fragment key={alien.id}>
            <Polyline positions={alien.decodedRoute} color="purple" dashArray="3" />
            <Marker
              position={alien.decodedRoute[alien.positionIdx]}
              icon={alienIcon(alien.id)}
            />
          </React.Fragment>
        ))}
      </MapContainer>
    </div>
  );
}
